arguments.step_map:
{%- set profile_mode = vars.ztp_profile_mode -%}
{%- set steps = {} -%}
{%- if "Manual" in profile_mode -%}
  {%- for s in vars.step_map_default -%}
    {%- set _do = steps.update({ s: {"next_step":"Complete"} }) -%}
  {%- endfor -%}
{%- elif vars.step_map_ztp_profile != "" -%}
  {%- set steps = vars.step_map_ztp_profile -%}  
{%- else -%}
  {%- set steps = vars.step_map_default -%}  
{%- endif -%}
{{steps}}

arguments.device_groups:
{%- set groups = [] -%}
{# Get Groups from the ZTP Profile #}
{%- if "deviceGroups" in vars.input.records[0].zTPProfiles 
      and vars.input.records[0].zTPProfiles.deviceGroups != '' 
      and vars.input.records[0].zTPProfiles.deviceGroups != None 
-%}
  {%- for g in vars.input.records[0].zTPProfiles.deviceGroups.split(",") -%}
    {%- if g not in groups -%}
      {%- set _do = groups.append(g) -%}
    {%- endif -%}
  {%- endfor -%}
{%- endif -%}
{# Get Groups from the Device Metadata #}
{%- set md = vars.input.records[0].deviceMetadata | toDict -%}
{%- if "device_groups" in md  -%}
  {%- for g in md['device_groups'].split(",") -%}
    {%- if g not in groups -%}
      {%- set _do = groups.append(g) -%}
    {%- endif -%}   
  {%- endfor -%}
{%- endif -%}
{{groups}}

arguments.step_map:
{%- set mymap = vars.step_map -%}
{%- set _do = mymap.update({ "Device Metadata":{"next_step":"Pending"}} ) -%}
{{mymap}}

