   - Device Metadata "Policy Package" set.
   - ZTP Profile
   - Current Device Policy Package
   - Device Name
#}
{%- set policy = vars.input.records[0].deviceName -%}
{%- set md = vars.input.records[0].deviceMetadata | toDict -%}

{# Device Record #}
{%- if vars.input.records[0].policyPackage != ''
      and vars.input.records[0].policyPackage != None
-%}
  {%- set policy = vars.input.records[0].policyPackage -%}
{%- endif -%}

{# ZTP Profile Record #}
{%- if "policyPackage" in vars.input.records[0].zTPProfiles 
      and vars.input.records[0].zTPProfiles.policyPackage != '' 
      and vars.input.records[0].zTPProfiles.policyPackage != None 
-%}
  {%- set policy = vars.input.records[0].zTPProfiles.policyPackage -%}
{%- endif -%}

{# Device Metadata #}
{%- if "policy_package" in md 
      and md['policy_package'] != ''
      and md['policy_package'] != None
-%}
  {%- set policy = md['policy_package'] -%}
{%- endif -%}

{{policy}}

arguments.metadata_monitor_test:
{# 
  Create an index of metafield matches. 
    match_count:
    no_match_count:
    results: field=true or false
#}
{%- set md = vars.device_metadata -%}
{%- set idx = {} -%}
{%- set _do = idx.update({"match_count":0,"no_match_count":0,"no_match_count":0}) -%}
{%- set mdrx_data = vars.device_metadata_monitor_regex -%}
{%- if mdrx_data != None -%}
  {%- set rxsearch = {} -%}
  {%- for rxk in mdrx_data -%}
    {%- if rxk in md -%}
      {%- if md[rxk] | regex_search(mdrx_data[rxk]) -%}
        {%- set _do = rxsearch.update({rxk:True}) -%}
          {%- set _do = idx.update({"match_count":idx.match_count+1}) -%}
        {% else -%}
          {%- set _do = rxsearch.update({rxk:False}) -%}
          {%- set _do = idx.update({"no_match_count":idx.no_match_count+1}) -%}
      {%- endif -%}
    {% endif -%}
  {%- endfor -%}
  {%- set _do = idx.update({"results":rxsearch}) -%}
{%- endif -%}
{{idx}}

